package Com.Loops;

public class Display7No {
	public void dispnum() {
		int i=401;
		while(i<=499)
		{
			System.out.println(i);
			i=i+7;
		}
	
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     Display7No obj= new Display7No();
     obj.dispnum();
	}

	
	}

