package Com.Loops;

public class SwapUsing3rdVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a=10;
     int b=20;
     System.out.println("Before Swap a "+a+" b " +b);
    
     int temp ;
     temp=a;
     a=b;
     b=temp;
     
     System.out.println(" After Swap a "+a+" b " +b);
     
	}

}
