package Com.Loops;

public class VowelOrConsonant {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     char ch = 'C';
     switch (ch)
     { 

     case 'A':
     case 'E':
     case 'I':
     case 'O':
     case 'U':
    	     System.out.println(ch+" is Vowel");
    	     break;
    	 default:
    		 System.out.println(ch+" is Consonant");	 
    	 
     }
	}

}
